import React, { useState } from 'react';
import './DatosContacto.css'; // Importamos el archivo CSS


var horario;
var horaActual = new Date();
var hora = horaActual.getHours();
var minutos = horaActual.getMinutes();
function mostrarDiaSegunHora() {
  // Obtenemos la hora actual

  // Establecemos el día a mostrar según la hora
  if (hora >= 9 && hora < 13) {
    horario = "Buenos días";
  } else if (hora >= 13 && hora < 20) {
    horario = "Buenas tardes";
  } else {
    horario = "Buenas noches";
  }

}



function DatosContacto({nota, correo}) {
    const [nombre, setNombre] = useState('');
    const [documento, setDocumento] = useState('');
    const [contacto, setContacto] = useState('');

    mostrarDiaSegunHora();

    let notaEdit = "";
    let correoEdit = "";
    if (nota) {
      notaEdit = nota.replace("{cliente}", nombre)
                      .replace("{dni}", documento)
                      .replace("{contacto}", contacto)
                      .replace("{horario}", horario)

      correoEdit = correo
    }

  return (
    <div id='datosContacto'>

      <div>
        <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} placeholder="Nombre" />
        <input type="text" value={documento} onChange={(e) => setDocumento(e.target.value)} placeholder="Documento de Identidad" />
        <input type="text" value={contacto} onChange={(e) => setContacto(e.target.value)} placeholder="Número de contacto" />
      </div>

      <div>
        <textarea type="text" value={notaEdit} placeholder="Nota..." />
        <button id="copiarBtnNota"><i class="material-icons">content_copy</i></button>

        <textarea type="text" value={correoEdit} placeholder="Correo..." />
        <button id="copiarBtnCorreo"><i class="material-icons">content_copy</i></button>
      </div>
    </div>
  );
}

export default DatosContacto;
