import React, { useState } from 'react';
import './DataList.css'; // Importamos el archivo CSS
import datosTickets from '../../Datos/Datos.js';
import DatosTickets from '../DatosTickets.js';

function DataList() {
  const [searchQuery, setSearchQuery] = useState('');
  // const options = ['Cambio de IBAN con distinto titular al del servicio', 
  //                  'Cambio de documento de identidad',
  //                  'Cambiar dirección de envío del regalo/terminal',
  //                  'Modificar dirección de envío de SIM',
  //                  'Impuesto incorrecto en factura - Si el cliente no tiene fibra activa',
  //                  'Impuesto incorrecto en factura - Cliente tiene fibra activa en ubicación del impuesto',
  //                  'Cambio de titular, cuando el cliente indique que no puede firmar el SMS',
  //                  'Incidencia portabilidad - AREC RECH_IDENT / AREC RECH_ BNUME o rechazo equivalente de LOWI'];

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  return (
    <div className="search-container">
      <input
        type="text"
        placeholder="Buscar incidencia..."
        value={searchQuery}
        onChange={handleInputChange}
        list="suggestions"
      />
      <datalist id="suggestions">

          {datosTickets.map((ticket) => (
            <option key={ticket.motivo} value={ticket.motivo} />
          ))}

      </datalist>

      <DatosTickets motivoSeleccionado={searchQuery} />
    </div>
  );
}

export default DataList;
