import React, { useState } from 'react';

function barraBusqueda() {
  const [busqueda, setBusqueda] = useState('');
  const [nombre, setNombre] = useState('');
  const [documento, setDocumento] = useState('');
  const [contacto, setContacto] = useState('');
  const [camposAdicionales, setCamposAdicionales] = useState([]);

  const handleBusqueda = (event) => {
    setBusqueda(event.target.value);
    // Aquí iría la lógica para determinar los campos adicionales basados en la búsqueda
    // Por ejemplo:
    if (busqueda === 'producto1') {
      setCamposAdicionales(['Tamaño', 'Color']);
    } else if (busqueda === 'producto2') {
      setCamposAdicionales(['Marca', 'Modelo']);
    } else {
      setCamposAdicionales([]);
    }
  };

  return (
    <div>
      <input type="text" value={busqueda} onChange={handleBusqueda} placeholder="Buscar" />
      <div>
        <input type="text" value={nombre} onChange={(e) => setNombre(e.target.value)} placeholder="Nombre" />
        <input type="text" value={documento} onChange={(e) => setDocumento(e.target.value)} placeholder="Documento de Identidad" />
        <input type="text" value={contacto} onChange={(e) => setContacto(e.target.value)} placeholder="Número de contacto" />
      </div>
      {camposAdicionales.map((campo, index) => (
        <div key={index}>
          <input type="text" placeholder={campo} />
        </div>
      ))}
    </div>
  );
}

export default barraBusqueda;
